INSERT INTO glossary_terms (term, definition, category, created_at) VALUES ('Bear Market', 'A market condition in which prices are falling or expected to fall. It typically describes a condition where securities prices fall 20% or more from recent highs.', 'Market Basics', NOW());
INSERT INTO glossary_terms (term, definition, category, created_at) VALUES ('Bull Market', 'A market condition where prices are rising or expected to rise. The term is typically used to refer to the stock market but can apply to anything traded, such as bonds, real estate, currencies, and commodities.', 'Market Basics', NOW());
INSERT INTO glossary_terms (term, definition, category, created_at) VALUES ('Dividend', 'A distribution of a portion of a company''s earnings to its shareholders. Dividends are typically paid quarterly and represent a return on investment for stockholders.', 'Income', NOW());
INSERT INTO glossary_terms (term, definition, category, created_at) VALUES ('ETF', 'Exchange-Traded Fund. A type of pooled investment security that operates much like a mutual fund but trades on stock exchanges like individual stocks.', 'Investment Vehicles', NOW());
INSERT INTO glossary_terms (term, definition, category, created_at) VALUES ('Market Cap', 'Market capitalization. The total market value of a company''s outstanding shares, calculated by multiplying the share price by the number of shares outstanding.', 'Valuation', NOW());
INSERT INTO glossary_terms (term, definition, category, created_at) VALUES ('P/E Ratio', 'Price-to-Earnings ratio. A valuation metric calculated by dividing the market price per share by earnings per share. It indicates how much investors are willing to pay per dollar of earnings.', 'Valuation', NOW());
INSERT INTO glossary_terms (term, definition, category, created_at) VALUES ('Short Selling', 'An investment strategy that speculates on the decline of a stock''s price. Traders borrow shares to sell at the current price, hoping to buy them back later at a lower price.', 'Trading', NOW());
INSERT INTO glossary_terms (term, definition, category, created_at) VALUES ('Volatility', 'A statistical measure of the dispersion of returns for a given security or market index. Higher volatility means greater price fluctuations and potentially higher risk.', 'Risk', NOW());
INSERT INTO quizzes (title, description, difficulty, created_at) VALUES ('Market Basics', 'Learn the fundamentals of stock markets, including bull and bear markets, trading volume, and market cycles.', 'BEGINNER', NOW());
INSERT INTO quizzes (title, description, difficulty, created_at) VALUES ('Valuation Metrics', 'Understand key valuation metrics used to analyze stocks, including P/E ratio, market cap, and dividend yield.', 'BEGINNER', NOW());
INSERT INTO quizzes (title, description, difficulty, created_at) VALUES ('Trading Strategies', 'Explore different trading strategies including day trading, swing trading, and position trading approaches.', 'INTERMEDIATE', NOW());
INSERT INTO quizzes (title, description, difficulty, created_at) VALUES ('Risk Management', 'Master essential risk management concepts including diversification, hedging, and portfolio allocation strategies.', 'INTERMEDIATE', NOW());
INSERT INTO quizzes (title, description, difficulty, created_at) VALUES ('Technical Analysis', 'Dive into technical analysis tools like moving averages, RSI, support and resistance levels.', 'ADVANCED', NOW());
INSERT INTO quizzes (title, description, difficulty, created_at) VALUES ('Global Markets', 'Understand how international markets interact, currency effects, and geopolitical factors influencing investments.', 'ADVANCED', NOW());
INSERT INTO quiz_questions (quiz_id, question, correct_answer, explanation, question_order, created_at) VALUES (1, 'What typically indicates a bull market?', 1, 'A bull market is characterized by rising prices or expectations of rising prices, typically associated with investor optimism and economic growth.', 0, NOW());
INSERT INTO quiz_questions (quiz_id, question, correct_answer, explanation, question_order, created_at) VALUES (1, 'A company with a high P/E ratio generally indicates:', 1, 'A high P/E ratio typically suggests that investors expect higher earnings growth in the future compared to companies with lower P/E ratios.', 1, NOW());
INSERT INTO quiz_questions (quiz_id, question, correct_answer, explanation, question_order, created_at) VALUES (1, 'What is the primary purpose of short selling?', 2, 'Short selling is a strategy used to profit from an expected decline in a stock''s price by borrowing and selling shares, then buying them back at a lower price.', 2, NOW());
INSERT INTO quiz_questions (quiz_id, question, correct_answer, explanation, question_order, created_at) VALUES (2, 'What does market capitalization represent?', 1, 'Market capitalization is calculated by multiplying the current share price by the total number of outstanding shares, representing the total market value of a company.', 0, NOW());
INSERT INTO quiz_questions (quiz_id, question, correct_answer, explanation, question_order, created_at) VALUES (2, 'A low P/E ratio compared to industry peers may suggest:', 1, 'A low P/E ratio relative to peers can indicate a stock is undervalued, though it may also reflect lower growth expectations or underlying issues.', 1, NOW());
INSERT INTO quiz_questions (quiz_id, question, correct_answer, explanation, question_order, created_at) VALUES (2, 'Dividend yield is calculated by dividing:', 1, 'Dividend yield is the annual dividend payment divided by the stock price, expressed as a percentage. It shows the return from dividends alone.', 2, NOW());
INSERT INTO quiz_questions (quiz_id, question, correct_answer, explanation, question_order, created_at) VALUES (3, 'What is a stop-loss order?', 1, 'A stop-loss order automatically sells a security when it reaches a specified price, limiting potential losses on a position.', 0, NOW());
INSERT INTO quiz_questions (quiz_id, question, correct_answer, explanation, question_order, created_at) VALUES (3, 'Dollar-cost averaging involves:', 1, 'Dollar-cost averaging means investing a fixed amount regularly regardless of price, which reduces the impact of volatility over time.', 1, NOW());
INSERT INTO quiz_questions (quiz_id, question, correct_answer, explanation, question_order, created_at) VALUES (3, 'What distinguishes swing trading from day trading?', 1, 'Swing trading holds positions for several days to weeks, aiming to capture short-to-medium-term price movements, while day trading closes all positions within the same day.', 2, NOW());
INSERT INTO quiz_questions (quiz_id, question, correct_answer, explanation, question_order, created_at) VALUES (4, 'What is the primary benefit of portfolio diversification?', 2, 'Diversification reduces unsystematic (company-specific) risk by spreading investments across different assets, sectors, and geographies.', 0, NOW());
INSERT INTO quiz_questions (quiz_id, question, correct_answer, explanation, question_order, created_at) VALUES (4, 'What does beta measure in investing?', 1, 'Beta measures a stock''s volatility relative to the overall market. A beta greater than 1 indicates higher volatility than the market.', 1, NOW());
INSERT INTO quiz_questions (quiz_id, question, correct_answer, explanation, question_order, created_at) VALUES (4, 'What is a hedging strategy?', 1, 'Hedging involves taking an offsetting investment position to reduce the risk of adverse price movements in an existing position.', 2, NOW());
INSERT INTO quiz_questions (quiz_id, question, correct_answer, explanation, question_order, created_at) VALUES (5, 'What does a "golden cross" pattern indicate?', 1, 'A golden cross occurs when a short-term moving average crosses above a long-term moving average, typically seen as a bullish signal.', 0, NOW());
INSERT INTO quiz_questions (quiz_id, question, correct_answer, explanation, question_order, created_at) VALUES (5, 'An RSI value above 70 generally indicates:', 1, 'An RSI (Relative Strength Index) above 70 suggests an asset may be overbought and could be due for a price correction or pullback.', 1, NOW());
INSERT INTO quiz_questions (quiz_id, question, correct_answer, explanation, question_order, created_at) VALUES (5, 'What is a support level?', 1, 'A support level is a price point where a stock tends to stop falling because buying interest increases, creating a floor for the price.', 2, NOW());
INSERT INTO quiz_questions (quiz_id, question, correct_answer, explanation, question_order, created_at) VALUES (6, 'How does a strengthening US dollar typically affect US exporters?', 1, 'A stronger US dollar makes American goods more expensive for foreign buyers, potentially reducing demand for US exports and hurting exporter revenues.', 0, NOW());
INSERT INTO quiz_questions (quiz_id, question, correct_answer, explanation, question_order, created_at) VALUES (6, 'What are emerging markets?', 1, 'Emerging markets are economies in the process of rapid growth and industrialization, offering higher growth potential but also carrying more risk than developed markets.', 1, NOW());
INSERT INTO quiz_questions (quiz_id, question, correct_answer, explanation, question_order, created_at) VALUES (6, 'What is geopolitical risk in investing?', 1, 'Geopolitical risk refers to the potential for political events, conflicts, or policy changes between nations to negatively impact investment returns.', 2, NOW());
